mapper.select:
select 

from book
where tel = #{tel,jdbcType=VARCHAR}
  and room_id = #{roomId,jdbcType=VARCHAR}

mapper.delete:
delete from book
where tel = #{tel,jdbcType=VARCHAR}
  and room_id = #{roomId,jdbcType=VARCHAR}

mapper.update:
update book


    bookdate = #{bookdate,jdbcType=INTEGER},


    is_booked = #{isBooked,jdbcType=INTEGER},


where tel = #{tel,jdbcType=VARCHAR}
  and room_id = #{roomId,jdbcType=VARCHAR}

update book
set bookdate = #{bookdate,jdbcType=INTEGER},
  is_booked = #{isBooked,jdbcType=INTEGER}
where tel = #{tel,jdbcType=VARCHAR}
  and room_id = #{roomId,jdbcType=VARCHAR}
